mapper.select:
SELECT CASE when eip.velocity_name is null then '' else eip.velocity_name end v1, CASE when eip1.velocity_name is null then '' else eip1.velocity_name end v2, CASE when eip2.velocity_name is null then '' else eip2.velocity_name end v3, CASE when eip3.velocity_name is null then '' else eip3.velocity_name end v4, CASE when eip4.velocity_name is null then '' else eip4.velocity_name end v5, CASE when eip.column_url is null then '' else eip.column_url end c1, CASE when eip1.column_url is null then '' else eip1.column_url end c2, CASE when eip2.column_url is null then '' else eip2.column_url end c3, CASE when eip3.column_url is null then '' else eip3.column_url end c4, CASE when eip4.column_url is null then '' else eip4.column_url end c5, CASE when eip.page_save_path is null then '' else eip.page_save_path end p1, CASE when eip1.page_save_path is null then '' else eip1.page_save_path end p2, CASE when eip2.page_save_path is null then '' else eip2.page_save_path end p3, CASE when eip3.page_save_path is null then '' else eip3.page_save_path end p4, CASE when eip4.page_save_path is null then '' else eip4.page_save_path end p5 FROM eph_infcenter_pilot eip LEFT JOIN eph_infcenter_pilot eip1 ON eip1.parent_id = eip.id LEFT JOIN eph_infcenter_pilot eip2 ON eip2.parent_id = eip1.id LEFT JOIN eph_infcenter_pilot eip3 ON eip3.parent_id = eip2.id LEFT JOIN eph_infcenter_pilot eip4 ON eip4.parent_id = eip3.id WHERE eip.id = #{websiteId,jdbcType=INTEGER} AND eip1.id = #{firstLevelId,jdbcType=INTEGER} AND eip2.id = #{secondLevelId,jdbcType=INTEGER}
AND eip3.id = #{thirdLevelId,jdbcType=INTEGER}
AND eip4.id = #{fourthLevelId,jdbcType=INTEGER}

SELECT eii.id, eii.title, eii.indexUrl, DATE_FORMAT(eii.operate_time,'%Y-%m-%d') operateTime FROM eph_infcenter_information eii where 1=1 and eii.is_effective=1
and eii.first_level_id=${firstLevelId}
and eii.isIndex=${isIndex}
order by eii.is_top desc,eii.operate_time desc
LIMIT ${rowNumber}

SELECT eii.id, eii.title, eii.operate_time operateTime FROM eph_infcenter_information eii where 1=1 and eii.is_effective=1
and eii.first_level_id=#{firstLevelId}
order by eii.is_top desc,eii.operate_time desc limit #{startIndex},#{pageSize}

SELECT count(1) FROM eph_infcenter_information eii where 1=1 and eii.is_effective=1
and eii.first_level_id=#{firstLevelId}
order by eii.is_top desc,eii.operate_time desc

select a.id,a.title,a.text,a.indexUrl,a.operate_time operateTime from eph_infcenter_information a where 1=1 and a.is_effective=1
and a.id=#{id}
ORDER BY a.operate_time LIMIT 1
